import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { FaStar, FaChevronLeft, FaChevronRight } from "react-icons/fa";
import { customerFeedback } from "../data/customerFeedback";

export default function CustomerFeedback() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);

  // Auto-rotate carousel
  useEffect(() => {
    if (!isAutoPlaying) return;

    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % customerFeedback.length);
    }, 5000); // Change slide every 5 seconds

    return () => clearInterval(interval);
  }, [isAutoPlaying]);

  const handlePrevious = () => {
    setCurrentIndex((prev) =>
      prev === 0 ? customerFeedback.length - 1 : prev - 1
    );
    setIsAutoPlaying(false);
  };

  const handleNext = () => {
    setCurrentIndex((prev) => (prev + 1) % customerFeedback.length);
    setIsAutoPlaying(false);
  };

  const handleDotClick = (index) => {
    setCurrentIndex(index);
    setIsAutoPlaying(false);
  };

  const currentFeedback = customerFeedback[currentIndex];

  return (
    <section className="py-16 px-6 bg-gradient-to-br from-primary/10 to-secondary/10">
      <div className="container mx-auto max-w-4xl">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="text-center mb-12"
        >
          <h2 className="text-4xl font-bold mb-2">What Our Clients Say</h2>
          <p className="text-lg opacity-70">
            Trusted by manufacturers worldwide
          </p>
        </motion.div>

        {/* Carousel Container */}
        <div className="relative">
          {/* Navigation Buttons */}
          <button
            onClick={handlePrevious}
            className="absolute left-0 top-1/2 -translate-y-1/2 -translate-x-4 md:-translate-x-12 z-10 btn btn-circle btn-primary shadow-lg"
            aria-label="Previous testimonial"
          >
            <FaChevronLeft />
          </button>

          <button
            onClick={handleNext}
            className="absolute right-0 top-1/2 -translate-y-1/2 translate-x-4 md:translate-x-12 z-10 btn btn-circle btn-primary shadow-lg"
            aria-label="Next testimonial"
          >
            <FaChevronRight />
          </button>

          {/* Testimonial Card */}
          <div className="bg-base-100 rounded-2xl shadow-2xl p-8 md:p-12 min-h-[320px] flex items-center justify-center">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentIndex}
                initial={{ opacity: 0, x: 50 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -50 }}
                transition={{ duration: 0.4 }}
                className="text-center"
              >
                {/* Avatar */}
                <div className="flex justify-center mb-6">
                  <img
                    src={currentFeedback.avatar}
                    alt={currentFeedback.name}
                    className="w-20 h-20 rounded-full border-4 border-primary shadow-lg"
                  />
                </div>

                {/* Rating Stars */}
                <div className="flex justify-center gap-1 mb-4">
                  {[...Array(currentFeedback.rating)].map((_, i) => (
                    <FaStar key={i} className="text-yellow-500 text-xl" />
                  ))}
                </div>

                {/* Feedback Text */}
                <p className="text-lg md:text-xl italic mb-6 leading-relaxed text-base-content/80">
                  "{currentFeedback.feedback}"
                </p>

                {/* Customer Info */}
                <div>
                  <h4 className="font-bold text-xl">{currentFeedback.name}</h4>
                  <p className="text-sm opacity-70">
                    {currentFeedback.role} at {currentFeedback.company}
                  </p>
                </div>
              </motion.div>
            </AnimatePresence>
          </div>

          {/* Navigation Dots */}
          <div className="flex justify-center gap-2 mt-8">
            {customerFeedback.map((_, index) => (
              <button
                key={index}
                onClick={() => handleDotClick(index)}
                className={`w-3 h-3 rounded-full transition-all ${
                  index === currentIndex
                    ? "bg-primary w-8"
                    : "bg-base-content/30 hover:bg-base-content/50"
                }`}
                aria-label={`Go to testimonial ${index + 1}`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

import { motion } from "framer-motion";
import { Link } from "react-router-dom";

export default function Hero() {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: "smooth", block: "start" });
    }
  };

  return (
    <section className="relative h-[85vh] flex items-center justify-center overflow-hidden">
      {/* Background Image with Overlay */}
      <div className="absolute inset-0 z-0">
        <img
          src="/src/assets/hero-banner.jpg"
          alt="Garment Production"
          className="w-full h-full object-cover"
          onError={(e) => {
            // Fallback gradient if image fails to load
            e.target.style.display = 'none';
            e.target.parentElement.style.background = 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)';
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-br from-primary/90 to-secondary/90"></div>
      </div>

      {/* Content */}
      <div className="relative z-10 px-6">
        <motion.div
          initial={{ opacity: 0, y: 40 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="text-center text-white max-w-4xl mx-auto"
        >
          {/* Logo */}
          <motion.div
            initial={{ scale: 0, rotate: -180 }}
            animate={{ scale: 1, rotate: 0 }}
            transition={{ delay: 0.1, duration: 0.6, type: "spring" }}
            className="text-7xl mb-6"
          >
            ðŸš¢
          </motion.div>

          {/* Main Heading */}
          <motion.h1
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.2, duration: 0.8 }}
            className="text-5xl md:text-7xl font-extrabold mb-6 leading-tight"
          >
            Welcome to InsightBoard
          </motion.h1>

          {/* Subtitle */}
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.4, duration: 0.8 }}
            className="text-xl md:text-2xl mb-10 opacity-95 leading-relaxed max-w-3xl mx-auto"
          >
            Streamline your garment production workflow with powerful order tracking and manufacturing management
          </motion.p>

          {/* CTA Buttons */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6, duration: 0.8 }}
            className="flex flex-col sm:flex-row gap-4 justify-center"
          >
            <motion.button
              onClick={() => scrollToSection("products-section")}
              whileHover={{ scale: 1.08 }}
              whileTap={{ scale: 0.95 }}
              className="btn btn-lg bg-white text-primary hover:bg-gray-100 border-none px-10 shadow-xl"
            >
              View Products
            </motion.button>

            <motion.button
              onClick={() => scrollToSection("how-it-works-section")}
              whileHover={{ scale: 1.08 }}
              whileTap={{ scale: 0.95 }}
              className="btn btn-lg btn-outline border-2 border-white text-white hover:bg-white hover:text-primary px-10"
            >
              Learn How It Works
            </motion.button>
          </motion.div>
        </motion.div>
      </div>

      {/* Scroll Indicator */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: 1.2, duration: 0.8 }}
        className="absolute bottom-8 left-1/2 transform -translate-x-1/2 z-10"
      >
        <motion.div
          animate={{ y: [0, 10, 0] }}
          transition={{ duration: 1.5, repeat: Infinity }}
          className="text-white text-4xl opacity-75"
        >
          â†“
        </motion.div>
      </motion.div>
    </section>
  );
}

import { motion } from "framer-motion";
import { FaSearch, FaClipboardCheck, FaCogs, FaTruck } from "react-icons/fa";

const steps = [
  {
    id: 1,
    icon: FaSearch,
    title: "Browse & Select",
    description:
      "Explore our wide range of production orders and manufacturing capabilities",
    color: "text-blue-500",
  },
  {
    id: 2,
    icon: FaClipboardCheck,
    title: "Place Order",
    description:
      "Submit your requirements with detailed specifications and customization options",
    color: "text-green-500",
  },
  {
    id: 3,
    icon: FaCogs,
    title: "Production Tracking",
    description:
      "Monitor real-time status updates through Cutting â†’ Sewing â†’ Finishing stages",
    color: "text-purple-500",
  },
  {
    id: 4,
    icon: FaTruck,
    title: "Quality & Delivery",
    description:
      "Final quality inspection and expedited shipment to your destination",
    color: "text-orange-500",
  },
];

export default function HowItWorks() {
  return (
    <section className="py-16 px-6 bg-base-100">
      <div className="container mx-auto max-w-6xl">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="text-center mb-12"
        >
          <h2 className="text-4xl font-bold mb-2">How It Works</h2>
          <p className="text-lg opacity-70">
            Simple steps to streamline your production workflow
          </p>
        </motion.div>

        {/* Steps Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {steps.map((step, index) => {
            const Icon = step.icon;
            return (
              <motion.div
                key={step.id}
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.15 }}
                viewport={{ once: true }}
                whileHover={{ y: -10 }}
                className="relative"
              >
                <div className="bg-base-200 rounded-lg p-6 h-full shadow-lg hover:shadow-2xl transition-shadow">
                  {/* Step Number */}
                  <div className="absolute -top-4 -left-4 w-10 h-10 bg-primary text-primary-content rounded-full flex items-center justify-center font-bold text-lg shadow-lg">
                    {step.id}
                  </div>

                  {/* Icon */}
                  <div className={`text-5xl ${step.color} mb-4 mt-2`}>
                    <Icon />
                  </div>

                  {/* Title */}
                  <h3 className="text-xl font-bold mb-3">{step.title}</h3>

                  {/* Description */}
                  <p className="text-sm opacity-70 leading-relaxed">
                    {step.description}
                  </p>
                </div>

                {/* Connector Arrow (hidden on last item and mobile) */}
                {index < steps.length - 1 && (
                  <div className="hidden lg:block absolute top-1/2 -right-4 transform -translate-y-1/2 text-3xl text-primary opacity-30">
                    â†’
                  </div>
                )}
              </motion.div>
            );
          })}
        </div>
      </div>
    </section>
  );
}

import { Navigate, Outlet, useLocation } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

/**
 * PrivateRoute
 * - Protects authenticated routes
 * - Supports multiple allowed roles
 */
const PrivateRoute = ({ allowedRoles }) => {
  const { user, loading } = useAuth();
  const location = useLocation();

  if (loading) return <p>Loading...</p>;

  // Not logged in â†’ redirect to login
  if (!user) return <Navigate to="/login" replace />;

  // Role check
  if (allowedRoles && !allowedRoles.includes(user.role)) {
    return <Navigate to="/unauthorized" replace />;
  }

  return <Outlet />;
};

export default PrivateRoute;

import { motion } from "framer-motion";
import { Link } from "react-router-dom";

export default function ProductCard({ product }) {
  const { name, price, image, description, _id } = product;

  // Truncate description to max 80 characters
  const truncatedDesc = description
    ? description.length > 80
      ? description.substring(0, 80) + "..."
      : description
    : "";

  return (
    <motion.div
      whileHover={{ y: -8 }}
      transition={{ duration: 0.3 }}
      className="card bg-base-100 shadow-xl hover:shadow-2xl transition-all overflow-hidden group"
    >
      <figure className="h-48 overflow-hidden">
        <motion.img
          whileHover={{ scale: 1.1 }}
          transition={{ duration: 0.4 }}
          src={image || "https://via.placeholder.com/400x300"}
          alt={name}
          className="w-full h-full object-cover"
        />
      </figure>

      <div className="card-body">
        <h2 className="card-title text-lg">{name}</h2>

        {/* Description */}
        {truncatedDesc && (
          <p className="text-sm opacity-70 mb-2">{truncatedDesc}</p>
        )}

        <p className="text-primary font-bold text-xl">${price}</p>

        <div className="card-actions justify-end mt-2">
          <Link to={`/products/${_id}`}>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="btn btn-primary btn-sm"
            >
              View Details
            </motion.button>
          </Link>
        </div>
      </div>
    </motion.div>
  );
}

import { createContext, useContext, useState, useEffect } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { toast } from "react-toastify";
import axios from "axios";
import {
  auth,
  registerWithEmail,
  loginWithEmail,
  loginWithGoogle,
  loginWithGithub,
  logoutUser,
  updateUserProfile,
} from "../services/firebaseAuth";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check backend authentication (for superadmin)
  const checkBackendAuth = async () => {
    try {
      const res = await axios.get(`${import.meta.env.VITE_API_URL}/auth/verify`, {
        withCredentials: true
      });
      
      if (res.data.user) {
        setUser(res.data.user);
        setLoading(false);
        return true;
      }
    } catch (error) {
      // No backend auth, will check Firebase
      return false;
    }
    return false;
  };

  // Listen to Firebase auth state changes AND check backend auth
  useEffect(() => {
    const initAuth = async () => {
      // First check if logged in via backend (superadmin)
      const hasBackendAuth = await checkBackendAuth();
      
      if (hasBackendAuth) {
        return; // Already authenticated via backend
      }

      // Otherwise, check Firebase auth
      const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
        if (firebaseUser) {
          setUser({
            uid: firebaseUser.uid,
            email: firebaseUser.email,
            displayName: firebaseUser.displayName,
            photoURL: firebaseUser.photoURL,
            role: firebaseUser.role || "user",
          });
        } else {
          setUser(null);
        }
        setLoading(false);
      });

      return () => unsubscribe();
    };

    initAuth();
  }, []);

  // Register with Email/Password
  const register = async (payload) => {
    try {
      const result = await registerWithEmail(payload);
      // Add role to user object (from registration form or default to 'user')
      setUser({ ...result.user, role: payload.role || "user" });
      toast.success("Account created successfully!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // Login with Email/Password
  const login = async (email, password) => {
    try {
      const result = await loginWithEmail(email, password);
      // Add default role. In production, fetch role from backend based on user ID
      setUser({ ...result.user, role: result.user.role || "user" });
      toast.success("Logged in successfully!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // Google OAuth
  const googleLogin = async () => {
    try {
      const result = await loginWithGoogle();
      const firebaseUser = result.user;
      // Properly extract photoURL from Firebase user object
      setUser({
        uid: firebaseUser.uid,
        email: firebaseUser.email,
        displayName: firebaseUser.displayName,
        photoURL: firebaseUser.photoURL,
        role: "user",
      });
      toast.success("Logged in with Google!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // GitHub OAuth
  const githubLogin = async () => {
    try {
      const result = await loginWithGithub();
      const firebaseUser = result.user;
      // Properly extract photoURL from Firebase user object
      setUser({
        uid: firebaseUser.uid,
        email: firebaseUser.email,
        displayName: firebaseUser.displayName,
        photoURL: firebaseUser.photoURL,
        role: "user",
      });
      toast.success("Logged in with GitHub!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // Logout
  const logout = async () => {
    try {
      await logoutUser();
      setUser(null);
      toast.success("Logged out successfully!");
    } catch (err) {
      toast.error("Logout failed");
      throw err;
    }
  };

  // Update profile (name / photo)
  const updateProfileInfo = async ({ displayName, photoURL }) => {
    try {
      const updated = await updateUserProfile({ displayName, photoURL });
      // keep existing role while refreshing other fields
      setUser((prev) => ({
        ...(prev || {}),
        ...updated,
        role: prev?.role || "user",
      }));
      toast.success("Profile updated");
      return updated;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  const value = {
    user,
    setUser,
    login,
    register,
    googleLogin,
    githubLogin,
    logout,
    updateProfileInfo,
    loading,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Helper function to get user-friendly error messages
const getFirebaseErrorMessage = (error) => {
  switch (error.code) {
    case "auth/email-already-in-use":
      return "This email is already registered";
    case "auth/invalid-email":
      return "Invalid email address";
    case "auth/weak-password":
      return "Password should be at least 6 characters";
    case "auth/user-not-found":
      return "No account found with this email";
    case "auth/wrong-password":
      return "Incorrect password";
    case "auth/invalid-credential":
      return "Invalid email or password. Please check your credentials and try again.";
    case "auth/popup-closed-by-user":
      return "Sign-in popup was closed";
    case "auth/cancelled-popup-request":
      return "Only one popup request is allowed at a time";
    case "auth/popup-blocked":
      return "Popup was blocked by browser. Please allow popups for this site";
    default:
      return error.message || "Authentication failed";
  }
};

// Custom hook to use auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

import { createContext, useContext, useEffect, useState } from "react";

const ThemeContext = createContext();

export const ThemeProvider = ({ children, storageKey = "theme", defaultTheme = "light" }) => {
  const [theme, setTheme] = useState(() => {
     return localStorage.getItem(storageKey) || defaultTheme;
  });

  useEffect(() => {
    localStorage.setItem(storageKey, theme);

    // Set data-theme attribute for DaisyUI
    document.documentElement.setAttribute("data-theme", theme);

    // Also set dark class for custom dark mode styles
    if (theme === "dark") {
      document.documentElement.classList.add("dark");
    } else {
      document.documentElement.classList.remove("dark");
    }
  }, [theme, storageKey]);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "light" ? "dark" : "light"));
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);

