import { useState, useEffect, useContext, createContext } from "react";

// Create Auth Context
const AuthContext = createContext();

// Provider Component
export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null); // logged-in user
  const [loading, setLoading] = useState(true); // for checking login state

  // Load user from localStorage on mount
  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      try {
        setUser(JSON.parse(storedUser));
      } catch (err) {
        localStorage.removeItem("user");
      }
    }
    setLoading(false);
  }, []);

  // Login function
  const login = (userData) => {
    setUser(userData);
    localStorage.setItem("user", JSON.stringify(userData));
  };

  // Logout function
  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
    localStorage.removeItem("token");
  };

  const value = { user, login, logout, loading };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Custom hook
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import axios from "axios";
import { toast } from "react-toastify";
import { useAuth } from "../context/AuthContext";

const Booking = () => {
    const { id } = useParams(); // product ID
    const navigate = useNavigate();
    const { user } = useAuth();
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);

    const { register, handleSubmit, watch, formState: { errors }, setValue } = useForm();
    
    // Watch quantity to calculate total price dynamically
    const watchQty = watch("qty", 0);
    const [totalPrice, setTotalPrice] = useState(0);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const res = await axios.get(`${import.meta.env.VITE_API_URL}/products/${id}`);
                setProduct(res.data);
                
                // Set default/min quantity
                setValue("qty", res.data.minQty || 1);
            } catch (err) {
                toast.error("Failed to load product details.");
                navigate("/products");
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id, navigate, setValue]);

    useEffect(() => {
        if (product) {
            setTotalPrice(product.price * watchQty);
        }
    }, [watchQty, product]);

    const onSubmit = async (data) => {
        try {
            const orderData = {
                productId: product._id,
                qty: Number(data.qty),
                shippingAddress: data.address,
                contactNumber: data.contact,
                receiverName: `${data.firstName} ${data.lastName}`,
                additionalNotes: data.notes,
                paymentMethod: data.paymentMethod
            };

            await axios.post(`${import.meta.env.VITE_API_URL}/bookings`, orderData, {
                withCredentials: true // Ensure cookies are sent if using cookie auth, or token header
                // If using header-based auth, ensure axios interceptor or header is set.
                // Assuming global axios setup or sticking to cookies as per plan.
            });

            toast.success("Booking placed successfully!");
            navigate("/dashboard/my-orders");
        } catch (err) {
            console.error(err);
            toast.error(err.response?.data?.message || "Failed to place order.");
        }
    };

    if (loading) return (
        <div className="flex justify-center items-center h-screen">
            <div className="loading loading-spinner loading-lg text-primary"></div>
        </div>
    );

    if (!product) return null;

    return (
        <div className="container mx-auto px-4 py-10 max-w-3xl">
            <h1 className="text-3xl font-bold mb-8 text-center text-gray-900 dark:text-white">Complete Your Booking</h1>
            
            <div className="bg-white dark:bg-gray-800 shadow-2xl rounded-2xl overflow-hidden flex flex-col md:flex-row">
                
                {/* Product Summary Sidebar (or Top) */}
                <div className="w-full md:w-1/3 bg-gray-50 dark:bg-gray-700 p-6 flex flex-col gap-4">
                    <h3 className="text-xl font-bold text-gray-800 dark:text-gray-100 border-b pb-2">Order Summary</h3>
                    <img src={product.images?.[0]} alt={product.title} className="w-full h-32 object-cover rounded-lg" />
                    <div>
                        <p className="font-semibold text-gray-700 dark:text-gray-300">{product.title}</p>
                        <p className="text-sm text-gray-500">{product.category}</p>
                    </div>
                    <div className="mt-auto pt-4 border-t dark:border-gray-600">
                        <div className="flex justify-between text-sm mb-1">
                            <span>Price per unit:</span>
                            <span>${product.price}</span>
                        </div>
                        <div className="flex justify-between text-lg font-bold text-primary">
                            <span>Total:</span>
                            <span>${totalPrice}</span>
                        </div>
                    </div>
                </div>

                {/* Booking Form */}
                <div className="w-full md:w-2/3 p-8">
                     <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
                        
                        {/* Read Only User Info */}
                        <div className="form-control">
                            <label className="label"><span className="label-text">Email</span></label>
                            <input type="email" value={user?.email || ""} readOnly className="input input-bordered w-full bg-gray-100 dark:bg-gray-600" />
                        </div>

                        {/* Name Fields */}
                        <div className="flex gap-4">
                            <div className="form-control w-1/2">
                                <label className="label"><span className="label-text">First Name</span></label>
                                <input 
                                    type="text" 
                                    {...register("firstName", { required: "First Name is required" })} 
                                    className="input input-bordered w-full" 
                                />
                                {errors.firstName && <span className="text-error text-xs mt-1">{errors.firstName.message}</span>}
                            </div>
                            <div className="form-control w-1/2">
                                <label className="label"><span className="label-text">Last Name</span></label>
                                <input 
                                    type="text" 
                                    {...register("lastName", { required: "Last Name is required" })} 
                                    className="input input-bordered w-full" 
                                />
                                {errors.lastName && <span className="text-error text-xs mt-1">{errors.lastName.message}</span>}
                            </div>
                        </div>

                        {/* Contact & Address */}
                        <div className="form-control">
                            <label className="label"><span className="label-text">Contact Number</span></label>
                            <input 
                                type="tel" 
                                {...register("contact", { required: "Contact Number is required" })} 
                                className="input input-bordered w-full" 
                            />
                            {errors.contact && <span className="text-error text-xs mt-1">{errors.contact.message}</span>}
                        </div>

                        <div className="form-control">
                            <label className="label"><span className="label-text">Delivery Address</span></label>
                            <textarea 
                                {...register("address", { required: "Address is required" })} 
                                className="textarea textarea-bordered h-24"
                            ></textarea>
                             {errors.address && <span className="text-error text-xs mt-1">{errors.address.message}</span>}
                        </div>

                        {/* Quantity */}
                         <div className="form-control">
                            <label className="label"><span className="label-text">Quantity (Min: {product.minQty}, Max: {product.qty})</span></label>
                            <input 
                                type="number" 
                                {...register("qty", { 
                                    required: "Quantity is required",
                                    min: { value: product.minQty, message: `Minimum quantity is ${product.minQty}` },
                                    max: { value: product.qty, message: `Maximum available is ${product.qty}` }
                                })} 
                                className="input input-bordered w-full" 
                            />
                             {errors.qty && <span className="text-error text-xs mt-1">{errors.qty.message}</span>}
                        </div>

                        {/* Payment Method */}
                        <div className="form-control">
                             <label className="label"><span className="label-text">Payment Method</span></label>
                             <select className="select select-bordered w-full" {...register("paymentMethod", { required: "Please select a payment method" })}>
                                <option value="">Select Option</option>
                                {product.paymentOptions?.map(opt => (
                                    <option key={opt} value={opt}>{opt}</option>
                                ))}
                             </select>
                             {errors.paymentMethod && <span className="text-error text-xs mt-1">{errors.paymentMethod.message}</span>}
                        </div>

                        {/* Notes */}
                        <div className="form-control">
                            <label className="label"><span className="label-text">Additional Notes (Optional)</span></label>
                            <textarea {...register("notes")} className="textarea textarea-bordered"></textarea>
                        </div>

                        <div className="mt-6">
                            <button type="submit" className="btn btn-primary w-full btn-lg">
                                Confirm & Place Order - ${totalPrice}
                            </button>
                        </div>

                     </form>
                </div>
            </div>
        </div>
    );
};

export default Booking;

import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";

const AdminAllOrders = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [filter, setFilter] = useState("all");
    const [search, setSearch] = useState("");

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const res = await axios.get(`${import.meta.env.VITE_API_URL}/bookings/all`, { withCredentials: true });
            setOrders(res.data);
        } catch (err) {
            toast.error("Failed to load orders");
        } finally {
            setLoading(false);
        }
    };

    const filteredOrders = orders.filter((o) => {
        const matchesFilter = filter === "all" ? true : o.status === filter;
        const term = search.toLowerCase();
        const matchesSearch =
            !term ||
            o._id?.toLowerCase().includes(term) ||
            o.receiverName?.toLowerCase().includes(term) ||
            o.product?.title?.toLowerCase().includes(term);
        return matchesFilter && matchesSearch;
    });

    if (loading) return <div className="text-center p-10">Loading orders...</div>;

    return (
        <div className="p-6">
            <div className="flex flex-col md:flex-row gap-4 md:items-center md:justify-between mb-6">
                <h1 className="text-3xl font-bold text-gray-800 dark:text-white">All Orders (Superadmin)</h1>
                <div className="flex flex-col sm:flex-row gap-3 w-full md:w-auto">
                    <input
                        type="text"
                        placeholder="Search by ID, buyer, product"
                        className="input input-bordered w-full sm:w-64 dark:bg-gray-800 dark:text-white"
                        value={search}
                        onChange={(e) => setSearch(e.target.value)}
                    />
                    <select className="select select-bordered dark:bg-gray-800 dark:text-white" value={filter} onChange={(e) => setFilter(e.target.value)}>
                        <option value="all">All Status</option>
                        <option value="pending">Pending</option>
                        <option value="approved">Approved</option>
                        <option value="rejected">Rejected</option>
                        <option value="processing">Processing</option>
                        <option value="shipped">Shipped</option>
                        <option value="delivered">Delivered</option>
                        <option value="cancelled">Cancelled</option>
                    </select>
                </div>
            </div>

            <div className="overflow-x-auto bg-white dark:bg-gray-800 rounded-lg shadow">
                 <table className="table w-full">
                    <thead>
                        <tr className="bg-gray-100 dark:bg-gray-700">
                            <th>Order ID</th>
                            <th>Product</th>
                            <th>User</th>
                            <th>Quantity</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredOrders.map(order => (
                            <tr key={order._id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                                <td className="text-xs font-mono">{order._id}</td>
                                <td>
                                    <div className="font-bold">{order.product?.title || "Unknown"}</div>
                                    <div className="text-xs text-gray-500 dark:text-gray-400">Price: ${order.total}</div>
                                </td>
                                <td>
                                    <div>{order.receiverName}</div>
                                    <div className="text-xs text-gray-500">{order.contactNumber}</div>
                                </td>
                                <td>{order.qty}</td>
                                <td>
                                    <span
                                        className={`badge badge-sm uppercase ${
                                            order.status === "pending"
                                                ? "badge-warning"
                                                : order.status === "approved"
                                                ? "badge-info"
                                                : order.status === "rejected"
                                                ? "badge-error"
                                                : "badge-ghost"
                                        }`}
                                    >
                                        {order.status}
                                    </span>
                                </td>
                                <td>
                                    <Link
                                        to={`/dashboard/track-order/${order._id}`}
                                        className="btn btn-xs btn-outline"
                                    >
                                        View
                                    </Link>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default AdminAllOrders;

import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const AdminAllProducts = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [updating, setUpdating] = useState(false);
    
    // Update Modal State
    const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [formData, setFormData] = useState({
        title: "",
        description: "",
        price: "",
        category: "",
        images: "",
        demoVideo: "",
        paymentOptions: ""
    });

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await axios.get(`${import.meta.env.VITE_API_URL}/products?limit=200`);
            setProducts(res.data.products);
        } catch (err) {
            toast.error("Failed to load products");
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if(!confirm("Are you sure you want to delete this product?")) return;
        try {
            await axios.delete(`${import.meta.env.VITE_API_URL}/products/${id}`, { withCredentials: true });
            toast.success("Product deleted");
            setProducts(products.filter(p => p._id !== id));
        } catch (err) {
            toast.error("Failed to delete product");
        }
    };

    const handleToggleHome = async (product) => {
        const next = !product.showOnHome;
        setUpdating(true);
        try {
            await axios.patch(
                `${import.meta.env.VITE_API_URL}/products/${product._id}`,
                { showOnHome: next },
                { withCredentials: true }
            );
            toast.success(`Marked "${product.title}" ${next ? "to show" : "to hide"} on home`);
            setProducts((prev) =>
                prev.map((p) =>
                    p._id === product._id ? { ...p, showOnHome: next } : p
                )
            );
        } catch (err) {
            toast.error("Failed to update home visibility");
        } finally {
            setUpdating(false);
        }
    };

    const openUpdateModal = (product) => {
        setSelectedProduct(product);
        setFormData({
            title: product.title || "",
            description: product.description || "",
            price: product.price || "",
            category: product.category || "",
            images: product.images?.join(", ") || "",
            demoVideo: product.demoVideo || "",
            paymentOptions: product.paymentOptions?.join(", ") || ""
        });
        setIsUpdateModalOpen(true);
    };

    const handleUpdateProduct = async () => {
        if (!formData.title || !formData.price || !formData.category) {
            toast.error("Please fill in all required fields");
            return;
        }

        try {
            const updateData = {
                title: formData.title,
                description: formData.description,
                price: parseFloat(formData.price),
                category: formData.category,
                images: formData.images.split(",").map(img => img.trim()).filter(Boolean),
                demoVideo: formData.demoVideo,
                paymentOptions: formData.paymentOptions.split(",").map(opt => opt.trim()).filter(Boolean)
            };

            await axios.patch(
                `${import.meta.env.VITE_API_URL}/products/${selectedProduct._id}`,
                updateData,
                { withCredentials: true }
            );

            toast.success("Product updated successfully");
            setIsUpdateModalOpen(false);
            fetchProducts();
        } catch (err) {
            toast.error(err.response?.data?.message || "Failed to update product");
        }
    };

    if (loading) return <div className="text-center p-10">Loading products...</div>;

    return (
        <div className="p-6">
            <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-white">All Products (Superadmin)</h1>

            <div className="overflow-x-auto bg-white dark:bg-gray-800 rounded-lg shadow">
                <table className="table w-full">
                    <thead>
                        <tr className="bg-gray-100 dark:bg-gray-700">
                            <th>Image</th>
                            <th>Product</th>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Created By</th>
                            <th>Show on Home</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product._id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                                <td>
                                    <div className="avatar">
                                        <div className="mask mask-squircle w-12 h-12">
                                            <img src={product.images?.[0] || "https://placehold.co/80"} alt={product.title} />
                                        </div>
                                    </div>
                                </td>
                                <td>
                                    <div className="font-bold">{product.title}</div>
                                    <div className="text-xs opacity-60 truncate max-w-[180px]">
                                        {product._id}
                                    </div>
                                </td>
                                <td>{product.category}</td>
                                <td>${product.price}</td>
                                <td>{product.createdBy || "â€”"}</td>
                                <td>
                                    <input
                                        type="checkbox"
                                        className="toggle toggle-primary"
                                        checked={!!product.showOnHome}
                                        onChange={() => handleToggleHome(product)}
                                        disabled={updating}
                                    />
                                </td>
                                <td className="space-x-2">
                                    <button
                                        className="btn btn-xs btn-outline btn-info"
                                        onClick={() => openUpdateModal(product)}
                                    >
                                        Update
                                    </button>
                                    <button className="btn btn-xs btn-error text-white" onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Update Product Modal */}
            {isUpdateModalOpen && selectedProduct && (
                <div className="modal modal-open">
                    <div className="modal-box max-w-2xl">
                        <h3 className="font-bold text-lg mb-4">Update Product</h3>
                        
                        <div className="space-y-4 max-h-[60vh] overflow-y-auto pr-2">
                            {/* Title */}
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Product Title *</span>
                                </label>
                                <input
                                    type="text"
                                    className="input input-bordered w-full"
                                    value={formData.title}
                                    onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                                    placeholder="Enter product title"
                                />
                            </div>

                            {/* Description */}
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Description</span>
                                </label>
                                <textarea
                                    className="textarea textarea-bordered h-24"
                                    value={formData.description}
                                    onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                                    placeholder="Enter product description"
                                ></textarea>
                            </div>

                            {/* Price and Category */}
                            <div className="grid grid-cols-2 gap-4">
                                <div className="form-control">
                                    <label className="label">
                                        <span className="label-text">Price *</span>
                                    </label>
                                    <input
                                        type="number"
                                        step="0.01"
                                        className="input input-bordered w-full"
                                        value={formData.price}
                                        onChange={(e) => setFormData({ ...formData, price: e.target.value })}
                                        placeholder="0.00"
                                    />
                                </div>

                                <div className="form-control">
                                    <label className="label">
                                        <span className="label-text">Category *</span>
                                    </label>
                                    <input
                                        type="text"
                                        className="input input-bordered w-full"
                                        value={formData.category}
                                        onChange={(e) => setFormData({ ...formData, category: e.target.value })}
                                        placeholder="e.g., T-Shirts"
                                    />
                                </div>
                            </div>

                            {/* Images */}
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Image URLs (comma-separated)</span>
                                </label>
                                <textarea
                                    className="textarea textarea-bordered h-20"
                                    value={formData.images}
                                    onChange={(e) => setFormData({ ...formData, images: e.target.value })}
                                    placeholder="https://image1.jpg, https://image2.jpg"
                                ></textarea>
                            </div>

                            {/* Demo Video */}
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Demo Video URL</span>
                                </label>
                                <input
                                    type="url"
                                    className="input input-bordered w-full"
                                    value={formData.demoVideo}
                                    onChange={(e) => setFormData({ ...formData, demoVideo: e.target.value })}
                                    placeholder="https://youtube.com/..."
                                />
                            </div>

                            {/* Payment Options */}
                            <div className="form-control">
                                <label className="label">
                                    <span className="label-text">Payment Options (comma-separated)</span>
                                </label>
                                <input
                                    type="text"
                                    className="input input-bordered w-full"
                                    value={formData.paymentOptions}
                                    onChange={(e) => setFormData({ ...formData, paymentOptions: e.target.value })}
                                    placeholder="Credit Card, PayPal, Cash"
                                />
                            </div>
                        </div>

                        <div className="modal-action mt-6">
                            <button className="btn" onClick={() => setIsUpdateModalOpen(false)}>Cancel</button>
                            <button className="btn btn-primary" onClick={handleUpdateProduct}>Save Changes</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdminAllProducts;

import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useAuth } from "../../../context/AuthContext";

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [search, setSearch] = useState("");
    const [roleFilter, setRoleFilter] = useState("");
    
    // For Suspend Modal
    const [selectedUser, setSelectedUser] = useState(null);
    const [suspendReason, setSuspendReason] = useState("");
    const [isvModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, [search, roleFilter]);

    const fetchUsers = async () => {
        try {
            setLoading(true);
            let url = `${import.meta.env.VITE_API_URL}/users?`;
            if (search) url += `search=${search}&`;
            if (roleFilter) url += `role=${roleFilter}`;

            const res = await axios.get(url, { withCredentials: true }); // Ensure cookie is sent
             // Or use header if auth implemented that way. Assuming cookie for now based on server setup.
            setUsers(res.data.users);
        } catch (err) {
            console.error(err);
            toast.error("Failed to load users");
        } finally {
            setLoading(false);
        }
    };

    const handleRoleChange = async (userId, newRole) => {
        if(!confirm(`Are you sure you want to change this user's role to ${newRole}?`)) return;
        try {
            await axios.patch(`${import.meta.env.VITE_API_URL}/users/${userId}/role`, { role: newRole }, { withCredentials: true });
            toast.success("User role updated");
            fetchUsers();
        } catch (err) {
            toast.error(err.response?.data?.message || "Failed to update role");
        }
    };

    const openSuspendModal = (user) => {
        setSelectedUser(user);
        setSuspendReason(user.suspendReason || ""); // Pre-fill if exists
        setIsModalOpen(true);
    };

    const handleSuspend = async () => {
        try {
            const status = selectedUser.status === 'suspended' ? 'active' : 'suspended';
            // Use the same endpoint as role change based on my controller update
            await axios.patch(`${import.meta.env.VITE_API_URL}/users/${selectedUser._id}/role`, { 
                status: status,
                suspendReason: status === 'suspended' ? suspendReason : "" 
            }, { withCredentials: true });

            toast.success(`User ${status === 'suspended' ? 'Suspended' : 'Activated'} Successfully`);
            setIsModalOpen(false);
            fetchUsers();
        } catch (err) {
            toast.error("Failed to update status");
        }
    };

    return (
        <div className="p-6 bg-base-200 min-h-screen">
            <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-white">Manage Users</h1>
            
            <div className="flex flex-col md:flex-row justify-between mb-6 gap-4">
                <input 
                    type="text" 
                    placeholder="Search by name or email..." 
                    className="input input-bordered w-full md:w-1/3"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                />
                <select 
                    className="select select-bordered w-full md:w-1/4"
                    value={roleFilter}
                    onChange={(e) => setRoleFilter(e.target.value)}
                >
                    <option value="">All Roles</option>
                    <option value="buyer">Buyer</option>
                    <option value="manager">Manager</option>
                </select>
            </div>

            <div className="overflow-x-auto bg-white dark:bg-gray-800 rounded-lg shadow">
                <table className="table w-full">
                    <thead>
                        <tr className="bg-gray-100 dark:bg-gray-700">
                            <th>Name</th>
                            <th>Email</th>
                            <th>Role</th>
                            <th>Status</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {users.map(user => (
                            <tr key={user._id} className="hover:bg-gray-50 dark:hover:bg-gray-700 transition">
                                <td className="font-semibold">{user.name}</td>
                                <td>{user.email}</td>
                                <td>
                                    <div className="badge badge-outline uppercase text-xs font-bold">
                                        {user.role}
                                    </div>
                                </td>
                                <td>
                                    {user.status === 'suspended' ? (
                                        <span className="badge badge-error gap-2">
                                            Suspended
                                        </span>
                                    ) : (
                                        <span className="badge badge-success gap-2">
                                            Active
                                        </span>
                                    )}
                                </td>
                                <td className="flex gap-2">
                                    {user.role !== 'superadmin' && (
                                        <>
                                            <select 
                                                className="select select-bordered select-xs"
                                                value={user.role}
                                                onChange={(e) => handleRoleChange(user._id, e.target.value)}
                                                disabled={user.role === 'superadmin'}
                                            >
                                                <option value="buyer">Buyer</option>
                                                <option value="manager">Manager</option>
                                            </select>

                                            <button 
                                                className={`btn btn-xs ${user.status === 'suspended' ? 'btn-success' : 'btn-error'}`}
                                                onClick={() => openSuspendModal(user)}
                                            >
                                                {user.status === 'suspended' ? 'Activate' : 'Suspend'}
                                            </button>
                                        </>
                                    )}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                {loading && <div className="text-center py-4">Loading users...</div>}
                {!loading && users.length === 0 && <div className="text-center py-4">No users found.</div>}
            </div>

            {/* Suspend Modal */}
            {isvModalOpen && selectedUser && (
                <div className="modal modal-open">
                    <div className="modal-box">
                        <h3 className="font-bold text-lg mb-4">
                            {selectedUser.status === 'suspended' ? 'Activate User' : 'Suspend User'}
                        </h3>
                        
                        {selectedUser.status !== 'suspended' && (
                            <div className="form-control">
                                <label className="label">Reason for Suspension</label>
                                <textarea 
                                    className="textarea textarea-bordered h-24"
                                    placeholder="e.g. Violation of terms..."
                                    value={suspendReason}
                                    onChange={(e) => setSuspendReason(e.target.value)}
                                ></textarea>
                            </div>
                        )}

                        {selectedUser.status === 'suspended' && (
                            <p>Are you sure you want to reactivate <strong>{selectedUser.name}</strong>?</p>
                        )}

                        <div className="modal-action">
                            <button className="btn" onClick={() => setIsModalOpen(false)}>Cancel</button>
                            <button 
                                className={`btn ${selectedUser.status === 'suspended' ? 'btn-success' : 'btn-error'}`}
                                onClick={handleSuspend}
                            >
                                Confirm
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageUsers;

import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";

const MyOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const res = await axios.get(`${import.meta.env.VITE_API_URL}/bookings/my-orders`, { withCredentials: true });
      setOrders(res.data);
    } catch (err) {
      toast.error("Failed to fetch orders");
    } finally {
      setLoading(false);
    }
  };

  const filteredOrders = orders.filter(order => {
    if (filter === "all") return true;
    if (filter === "active") return ["pending", "processing", "shipped"].includes(order.status);
    if (filter === "completed") return order.status === "delivered";
    if (filter === "cancelled") return order.status === "cancelled" || order.status === "rejected";
    return true;
  });

  const getStatusColor = (status) => {
    switch (status) {
        case "pending": return "badge-warning";
        case "processing": return "badge-info";
        case "shipped": return "badge-primary";
        case "delivered": return "badge-success";
        case "cancelled": return "badge-error";
        case "rejected": return "badge-error";
        default: return "badge-ghost";
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-white">My Orders</h1>

      {/* Tabs */}
      <div className="tabs tabs-boxed mb-6 bg-gray-100 dark:bg-gray-700">
        <a className={`tab ${filter === 'all' ? 'tab-active' : ''}`} onClick={() => setFilter('all')}>All Orders</a>
        <a className={`tab ${filter === 'active' ? 'tab-active' : ''}`} onClick={() => setFilter('active')}>Active</a>
        <a className={`tab ${filter === 'completed' ? 'tab-active' : ''}`} onClick={() => setFilter('completed')}>Completed</a>
        <a className={`tab ${filter === 'cancelled' ? 'tab-active' : ''}`} onClick={() => setFilter('cancelled')}>Cancelled</a>
      </div>

      {/* Orders List */}
      {loading ? (
        <div className="flex justify-center"><span className="loading loading-spinner loading-lg"></span></div>
      ) : filteredOrders.length > 0 ? (
        <div className="space-y-4">
            {filteredOrders.map(order => (
                <div key={order._id} className="card bg-base-100 shadow-xl border border-gray-200 dark:border-gray-700">
                    <div className="card-body p-6">
                        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-4">
                            <div>
                                <h3 className="font-bold text-lg mb-1">
                                    {order.product?.title || "Product Unavailable"} 
                                    <span className="text-sm font-normal text-gray-500 ml-2">(Qty: {order.qty})</span>
                                </h3>
                                <p className="text-sm text-gray-500">Order ID: {order._id}</p>
                            </div>
                            <div className="flex items-center gap-3 mt-2 md:mt-0">
                                <span className={`badge ${getStatusColor(order.status)} badge-lg capitalize`}>{order.status}</span>
                                <span className="font-bold text-xl text-primary">${order.total}</span>
                            </div>
                        </div>

                        <div className="divider my-0"></div>

                        <div className="flex flex-col md:flex-row justify-between items-center mt-4">
                            <div className="text-sm text-gray-600 dark:text-gray-400">
                                <p>Ordered on: {new Date(order.createdAt).toLocaleDateString()}</p> 
                                {order.tracking && order.tracking.length > 0 && (
                                    <p className="mt-1 text-info">
                                        Last Update: {order.tracking[order.tracking.length - 1].status} 
                                        ({new Date(order.tracking[order.tracking.length - 1].date).toLocaleDateString()})
                                    </p>
                                )}
                            </div>
                            
                            <div className="mt-4 md:mt-0 flex gap-2">
                                <Link to={`/dashboard/track-order/${order._id}`} className="btn btn-sm btn-outline btn-info">
                                    Track Order
                                </Link>
                                {order.status === 'pending' && (
                                    <button className="btn btn-sm btn-outline btn-error" onClick={() => toast.info("Cancellation logic to be implemented")}>
                                        Cancel Order
                                    </button>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            ))}
        </div>
      ) : (
        <div className="text-center py-10 bg-gray-50 dark:bg-gray-800 rounded-lg">
            <p className="text-gray-500">No orders found in this category.</p>
            <Link to="/products" className="btn btn-primary mt-4">Browse Products</Link>
        </div>
      )}
    </div>
  );
};

export default MyOrders;

import React, { useState } from "react";
import { useForm } from "react-hook-form";
import axios from "axios";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";

const AddProduct = () => {
  const { register, handleSubmit, reset, formState: { errors } } = useForm();
  const [images, setImages] = useState([]);
  const [currImage, setCurrImage] = useState("");
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);

  // Add image URL
  const handleAddImage = (e) => {
    e.preventDefault();
    if (currImage.trim() !== "") {
      setImages([...images, currImage]);
      setCurrImage("");
    }
  };

  // Remove image
  const handleRemoveImage = (index) => {
    setImages(images.filter((_, i) => i !== index));
  };

  const onSubmit = async (data) => {
    try {
        setLoading(true);
        if(images.length === 0 && !data.imageUrl) {
            toast.error("Please add at least one image");
            setLoading(false);
            return;
        }

        const finalImages = images.length > 0 ? images : [data.imageUrl];

        const payload = {
            ...data,
            images: finalImages,
            paymentOptions: ["cash", "online"] // Simplified for now or can collect from checkboxes
        };

        // Handle Payment Options from Select if needed or Checkboxes
        // Assuming simple default for now based on requirement "Payment Options (Cash, Online, Both)"
        if (data.payment === "cash") payload.paymentOptions = ["Cash on Delivery"];
        if (data.payment === "online") payload.paymentOptions = ["Online Payment"];
        if (data.payment === "both") payload.paymentOptions = ["Cash on Delivery", "Online Payment"];

        await axios.post(`${import.meta.env.VITE_API_URL}/products`, payload, { withCredentials: true });
        toast.success("Product added successfully!");
        navigate("/products"); // Or Redirect to Manage Products
    } catch (err) {
        toast.error(err.response?.data?.message || "Failed to add product");
    } finally {
        setLoading(false);
    }
  };

  return (
    <div className="p-6 max-w-2xl mx-auto bg-white dark:bg-gray-800 rounded-lg shadow-xl mt-10">
      <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-white text-center">Add New Product</h1>

      <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
        
        {/* Title */}
        <div className="form-control">
            <label className="label font-bold">Product Title</label>
            <input
              type="text"
              {...register("title", { required: "Title is required" })}
              className="input input-bordered w-full"
              placeholder="e.g. Premium Wireless Headphones"
            />
            {errors.title && <span className="text-red-500 text-sm">{errors.title.message}</span>}
        </div>

        {/* Description */}
        <div className="form-control">
            <label className="label font-bold">Description</label>
            <textarea
              {...register("desc", { required: "Description is required" })}
              className="textarea textarea-bordered h-24"
              placeholder="Product details..."
            />
            {errors.desc && <span className="text-red-500 text-sm">{errors.desc.message}</span>}
        </div>

        {/* Price & Qty Row */}
        <div className="flex flex-col md:flex-row gap-4">
            <div className="form-control w-full">
                <label className="label font-bold">Price ($)</label>
                <input
                  type="number"
                  {...register("price", { required: "Price is required", min: 1 })}
                  className="input input-bordered w-full"
                />
                {errors.price && <span className="text-red-500 text-sm">{errors.price.message}</span>}
            </div>

            <div className="form-control w-full">
                <label className="label font-bold">Quantity</label>
                <input
                  type="number"
                  {...register("qty", { required: "Quantity is required", min: 0 })}
                  className="input input-bordered w-full"
                />
                 {errors.qty && <span className="text-red-500 text-sm">{errors.qty.message}</span>}
            </div>
        </div>

        {/* Min Qty & Category Row */}
        <div className="flex flex-col md:flex-row gap-4">
            <div className="form-control w-full">
                <label className="label font-bold">Min Order Qty</label>
                <input
                  type="number"
                  {...register("minQty", { required: "Min Qty is required", min: 1 })}
                  className="input input-bordered w-full"
                  defaultValue={1}
                />
                 {errors.minQty && <span className="text-red-500 text-sm">{errors.minQty.message}</span>}
            </div>

             <div className="form-control w-full">
                <label className="label font-bold">Category</label>
                <select
                  {...register("category", { required: "Category is required" })}
                  className="select select-bordered w-full"
                >
                    <option value="">Select Category</option>
                    <option value="Men">Men</option>
                    <option value="Women">Women</option>
                    <option value="Kids">Kids</option>
                    <option value="Accessories">Accessories</option>
                    <option value="Electronics">Electronics</option>
                    <option value="Home">Home</option>
                </select>
                {errors.category && <span className="text-red-500 text-sm">{errors.category.message}</span>}
            </div>
        </div>

        {/* Payment Options & Show On Home */}
        <div className="flex flex-col md:flex-row gap-4">
            <div className="form-control w-full">
                <label className="label font-bold">Payment Options</label>
                <select
                  {...register("payment", { required: "Payment option is required" })}
                  className="select select-bordered w-full"
                >
                  <option value="both">Both (Cash & Online)</option>
                  <option value="cash">Cash Only</option>
                  <option value="online">Online Only</option>
                </select>
            </div>
             <div className="form-control w-full items-start pt-8">
                 <label className="label cursor-pointer gap-2">
                    <input type="checkbox" {...register("showOnHome")} className="checkbox checkbox-primary" />
                    <span className="label-text font-bold">Show on Home Page?</span>
                </label>
            </div>
        </div>
        
        {/* Demo Video */}
         <div className="form-control">
            <label className="label font-bold">Demo Video URL (Optional)</label>
            <input
              type="text"
              {...register("demoVideo")}
              className="input input-bordered w-full"
              placeholder="https://youtube.com/..."
            />
        </div>

        {/* Image Management */}
        <div className="form-control p-4 border rounded-lg bg-gray-50 dark:bg-gray-700">
          <label className="label font-bold">Product Images</label>
          <div className="flex gap-2 mb-2">
            <input
              type="text"
              value={currImage}
              onChange={(e) => setCurrImage(e.target.value)}
              placeholder="Paste Image URL"
              className="input input-bordered w-full"
            />
            <button onClick={handleAddImage} className="btn btn-secondary">Add</button>
          </div>
          
           {/* Previews */}
           <div className="flex flex-wrap gap-4 mt-2">
            {images.map((img, i) => (
              <div key={i} className="relative group">
                <img src={img} alt="preview" className="w-24 h-24 object-cover rounded border border-gray-300" />
                <button
                  type="button"
                  onClick={() => handleRemoveImage(i)}
                  className="absolute -top-2 -right-2 btn btn-circle btn-xs btn-error opacity-0 group-hover:opacity-100 transition-opacity"
                >âœ•</button>
              </div>
            ))}
          </div>
          <p className="text-xs text-gray-500 mt-2">Add at least one image URL.</p>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={loading}
          className={`btn btn-primary w-full text-lg mt-6 ${loading ? 'loading' : ''}`}
        >
          {loading ? 'Adding Product...' : 'Create Product'}
        </button>

      </form>
    </div>
  );
};

export default AddProduct;

import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const ApprovedOrders = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [selectedOrder, setSelectedOrder] = useState(null);
    
    // Tracking Form
    const [trackNote, setTrackNote] = useState("");
    const [trackLocation, setTrackLocation] = useState("");
    const [trackStatus, setTrackStatus] = useState("Shipped");

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const res = await axios.get(`${import.meta.env.VITE_API_URL}/bookings/approved`, { withCredentials: true });
            setOrders(res.data);
        } catch (err) {
            toast.error("Failed to load approved orders");
        } finally {
            setLoading(false);
        }
    };

    const handleUpdateTracking = async (e) => {
        e.preventDefault();
        try {
            await axios.put(`${import.meta.env.VITE_API_URL}/bookings/${selectedOrder._id}/tracking`, {
                status: trackStatus,
                location: trackLocation,
                note: trackNote
            }, { withCredentials: true });
            
            toast.success("Tracking Updated");
            setSelectedOrder(null);
            fetchOrders(); // Refresh to show new status if changed
        } catch (err) {
            toast.error("Failed to update tracking");
        }
    };

    if (loading) return <div className="p-10 text-center">Loading...</div>;

    return (
        <div className="p-6">
             <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-white">Active Orders</h1>
             
             <div className="overflow-x-auto bg-white dark:bg-gray-800 rounded-lg shadow">
                <table className="table w-full">
                    <thead>
                        <tr className="bg-gray-100 dark:bg-gray-700">
                            <th>Order ID</th>
                            <th>Product</th>
                            <th>Customer</th>
                            <th>Current Status</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map(order => (
                            <tr key={order._id}>
                                <td className="text-xs">{order._id}</td>
                                <td>
                                    <div className="font-bold">{order.product?.title}</div>
                                    <div className="text-xs">Qty: {order.qty}</div>
                                </td>
                                <td>
                                    <div>{order.receiverName}</div>
                                    <div className="text-xs text-gray-500">{order.contactNumber}</div>
                                </td>
                                <td><span className="badge badge-info">{order.status}</span></td>
                                <td>
                                    <button 
                                        className="btn btn-sm btn-outline"
                                        onClick={() => setSelectedOrder(order)}
                                    >
                                        Update Tracking
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
             </div>

             {/* Tracking Modal */}
             {selectedOrder && (
                <div className="modal modal-open">
                    <div className="modal-box">
                        <h3 className="font-bold text-lg mb-4">Update Tracking for #{selectedOrder._id.slice(-6)}</h3>
                        <form onSubmit={handleUpdateTracking} className="space-y-4">
                            
                            <div className="form-control">
                                <label className="label">Status Update</label>
                                <select className="select select-bordered" value={trackStatus} onChange={(e) => setTrackStatus(e.target.value)}>
                                    <option value="Processing">Processing</option>
                                    <option value="Shipped">Shipped</option>
                                    <option value="Out for Delivery">Out for Delivery</option>
                                    <option value="Delivered">Delivered</option>
                                </select>
                            </div>

                            <div className="form-control">
                                <label className="label">Current Location</label>
                                <input className="input input-bordered" placeholder="e.g. Dhaka Distribution Center" value={trackLocation} onChange={(e) => setTrackLocation(e.target.value)} required />
                            </div>

                            <div className="form-control">
                                <label className="label">Note</label>
                                <input className="input input-bordered" placeholder="e.g. Package arrived at facility" value={trackNote} onChange={(e) => setTrackNote(e.target.value)} required />
                            </div>

                            <div className="modal-action">
                                <button type="button" className="btn" onClick={() => setSelectedOrder(null)}>Cancel</button>
                                <button type="submit" className="btn btn-primary">Update</button>
                            </div>
                        </form>
                    </div>
                </div>
             )}
        </div>
    );
};

export default ApprovedOrders;

import { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";

const ManageProducts = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            // Assuming GET /products returns all products, but for manager maybe logic needs filtering? 
            // Or use a query param ?createdBy=me or filter client side.
            // For now, fetching all (or backend could have a /my-products endpoint).
            // Let's use standard list and filter by CreatedBy if user ID is available, 
            // OR just List All since Managers manage the store. 
            // "Manager Dashboard: Manage Products".
            // Let's stick to listing all for now as Managers likely manage the whole inventory.
            const res = await axios.get(`${import.meta.env.VITE_API_URL}/products?limit=100`); 
            setProducts(res.data.products);
        } catch (err) {
            toast.error("Failed to load products");
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if(!confirm("Are you sure you want to delete this product?")) return;
        try {
            await axios.delete(`${import.meta.env.VITE_API_URL}/products/${id}`, { withCredentials: true });
            toast.success("Product deleted");
            setProducts(products.filter(p => p._id !== id));
        } catch (err) {
            toast.error("Failed to delete product");
        }
    };

    if (loading) return <div className="text-center p-10">Loading products...</div>;

    return (
        <div className="p-6">
            <div className="flex justify-between items-center mb-6">
                <h1 className="text-3xl font-bold text-gray-800 dark:text-white">Manage Products</h1>
                <Link to="/dashboard/add-product" className="btn btn-primary">+ Add New Product</Link>
            </div>

            <div className="overflow-x-auto bg-white dark:bg-gray-800 rounded-lg shadow">
                <table className="table w-full">
                    <thead>
                        <tr className="bg-gray-100 dark:bg-gray-700">
                            <th>Image</th>
                            <th>Title</th>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Stock</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product._id} className="hover:bg-gray-50 dark:hover:bg-gray-700">
                                <td>
                                    <div className="avatar">
                                        <div className="mask mask-squircle w-12 h-12">
                                            <img src={product.images?.[0] || "https://placehold.co/50"} alt={product.title} />
                                        </div>
                                    </div>
                                </td>
                                <td className="font-semibold">{product.title}</td>
                                <td>{product.category}</td>
                                <td>${product.price}</td>
                                <td>
                                    {product.qty === 0 ? <span className="text-error font-bold">Out of Stock</span> : product.qty}
                                </td>
                                <td className="flex gap-2">
                                    {/* Edit Logic could be a modal or separate page. For brevity, skipping logic or placeholder */}
                                    <button className="btn btn-sm btn-ghost" onClick={() => toast.info("Edit functionality coming soon")}>Edit</button>
                                    <button className="btn btn-sm btn-error text-white" onClick={() => handleDelete(product._id)}>Delete</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                 {products.length === 0 && <div className="text-center py-4">No products found.</div>}
            </div>
        </div>
    );
};

export default ManageProducts;

import { useEffect, useState } from "react";
import axios from "axios";
import { toast } from "react-toastify";

const PendingOrders = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchOrders();
    }, []);

    const fetchOrders = async () => {
        try {
            const res = await axios.get(`${import.meta.env.VITE_API_URL}/bookings/pending`, { withCredentials: true });
            setOrders(res.data);
        } catch (err) {
            toast.error("Failed to load pending orders");
        } finally {
            setLoading(false);
        }
    };

    const handleStatus = async (id, status) => {
        if(!confirm(`Are you sure you want to ${status} this order?`)) return;
        try {
            await axios.put(`${import.meta.env.VITE_API_URL}/bookings/${id}/status`, { status }, { withCredentials: true });
            toast.success(`Order ${status} successfully`);
            setOrders(orders.filter(o => o._id !== id));
        } catch (err) {
            toast.error(err.response?.data?.message || "Failed to update status");
        }
    };

    if (loading) return <div className="p-10 text-center">Loading...</div>;

    return (
        <div className="p-6">
             <h1 className="text-3xl font-bold mb-6 text-gray-800 dark:text-white">Pending Orders</h1>
             
             <div className="grid gap-4">
                {orders.map(order => (
                    <div key={order._id} className="card bg-white dark:bg-gray-800 shadow-lg border border-l-4 border-l-warning p-4">
                        <div className="flex flex-col md:flex-row justify-between">
                            <div className="flex-1">
                                <h3 className="font-bold text-lg">{order.product?.title} (x{order.qty})</h3>
                                <p className="text-gray-500 text-sm">Order ID: {order._id}</p>
                                <p className="mt-2 font-semibold">Total: ${order.total}</p>
                                <div className="text-sm mt-2">
                                    <p><strong>Customer:</strong> {order.receiverName}</p>
                                    <p><strong>Address:</strong> {order.shippingAddress}</p>
                                    <p><strong>Contact:</strong> {order.contactNumber}</p>
                                </div>
                            </div>
                            <div className="flex flex-col gap-2 justify-center mt-4 md:mt-0">
                                <button onClick={() => handleStatus(order._id, "processing")} className="btn btn-success btn-sm text-white">Approve</button>
                                <button onClick={() => handleStatus(order._id, "rejected")} className="btn btn-error btn-sm text-white">Reject</button>
                            </div>
                        </div>
                    </div>
                ))}
                
                {orders.length === 0 && <div className="text-center py-10 bg-gray-50 dark:bg-gray-700 rounded-lg">No pending orders.</div>}
             </div>
        </div>
    );
};

export default PendingOrders;

import { useEffect, useState } from "react";
import { useForm } from "react-hook-form";
import { toast } from "react-toastify";
import { useAuth } from "../../context/AuthContext";

const Profile = () => {
  const { user, updateProfileInfo } = useAuth();
  const [saving, setSaving] = useState(false);

  const { register, handleSubmit, reset } = useForm({
    defaultValues: {
      displayName: user?.displayName || "",
      email: user?.email || "",
      photoURL: user?.photoURL || "",
    },
  });

  useEffect(() => {
    reset({
      displayName: user?.displayName || "",
      email: user?.email || "",
      photoURL: user?.photoURL || "",
    });
  }, [user, reset]);

  const onSubmit = (data) => {
    if (!user) return;
    setSaving(true);
    updateProfileInfo({
      displayName: data.displayName?.trim() || user.displayName,
      photoURL: data.photoURL?.trim() || user.photoURL || null,
    })
      .catch(() => {})
      .finally(() => setSaving(false));
  };

  return (
    <div className="max-w-4xl mx-auto text-gray-900 dark:text-gray-100">
      {/* Page Title */}
      <h1 className="text-3xl font-bold mb-8 text-gray-800 dark:text-white">
        My Profile
      </h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {/* ================= LEFT COLUMN ================= */}
        <div className="bg-white dark:bg-gray-800 shadow-lg border border-purple-200 dark:border-gray-700 rounded-2xl p-6 text-center">
          {/* Profile Avatar */}
          <div className="relative inline-block">
            <div className="w-32 h-32 rounded-full overflow-hidden mx-auto border-4 border-purple-100 dark:border-gray-600 shadow bg-gradient-to-br from-purple-500 to-blue-500 flex items-center justify-center text-4xl text-white font-bold">
              {user?.photoURL ? (
                <img
                  src={user.photoURL}
                  alt="Profile"
                  className="w-full h-full object-cover"
                  referrerPolicy="no-referrer"
                  crossOrigin="anonymous"
                  onError={(e) => {
                    e.currentTarget.style.display = "none";
                  }}
                />
              ) : null}
              {!user?.photoURL && (
                <span>{user?.displayName?.[0]?.toUpperCase() || "U"}</span>
              )}
            </div>

            {/* Online Dot */}
            <div
              className="absolute bottom-1 right-2 w-4 h-4 rounded-full bg-green-500 border-2 
              border-white dark:border-gray-800"
              title="Active"
            />
          </div>

          {/* Name */}
          <h2 className="mt-4 text-xl font-bold text-gray-800 dark:text-white">
            {user?.displayName || "User"}
          </h2>

          {/* Email */}
          <p className="text-gray-500 dark:text-gray-400 text-sm">
            {user?.email}
          </p>

          {/* Role Badge */}
          <div className="mt-3">
            <span className="px-3 py-1 text-xs rounded-full bg-purple-100 text-purple-700 dark:bg-purple-900/40 dark:text-purple-300 capitalize">
              {user?.role || "user"}
            </span>
          </div>

          {/* Suspended Alert */}
          {user?.status === "suspended" && (
            <div className="mt-4 text-sm py-2 px-3 bg-red-100 text-red-700 border border-red-300 rounded-lg dark:bg-red-900/30 dark:text-red-300 dark:border-red-700">
              âš ï¸ Account Suspended
            </div>
          )}
        </div>

        {/* ================= RIGHT COLUMN ================= */}
        <div className="md:col-span-2 bg-white dark:bg-gray-800 shadow-lg border border-purple-200 dark:border-gray-700 rounded-2xl p-8">
          {/* Heading */}
          <h3 className="text-xl font-semibold mb-6 text-gray-800 dark:text-white flex items-center gap-2">
            <span>âš™ï¸</span> Edit Profile
          </h3>

          {/* Form */}
          <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
            {/* Full Name */}
            <div>
              <label className="block mb-1 font-medium text-gray-700 dark:text-gray-300">
                Full Name
              </label>
              <input
                type="text"
                {...register("displayName")}
                className="input input-bordered w-full dark:bg-gray-700 dark:text-white"
              />
            </div>

            {/* Email (read-only) */}
            <div>
              <label className="block mb-1 font-medium text-gray-700 dark:text-gray-300">
                Email Address
              </label>
              <input
                type="email"
                {...register("email")}
                disabled
                className="input input-bordered w-full bg-gray-100 dark:bg-gray-600 dark:text-gray-300 cursor-not-allowed"
              />
              <p className="text-xs text-gray-400 mt-1">
                Email cannot be changed.
              </p>
            </div>

            {/* Image URL */}
            <div>
              <label className="block mb-1 font-medium text-gray-700 dark:text-gray-300">
                Profile Image URL
              </label>
              <input
                type="url"
                {...register("photoURL")}
                placeholder="https://example.com/avatar.jpg"
                className="input input-bordered w-full dark:bg-gray-700 dark:text-white"
              />
            </div>

            {/* Submit Button */}
              <button
                type="submit"
                className="btn btn-primary w-full md:w-auto"
                disabled={saving}
              >
                {saving ? "Saving..." : "Save Changes"}
              </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Profile;

import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import Hero from "../components/Hero";
import ProductCard from "../components/ProductCard";
import HowItWorks from "../components/HowItWorks";
import CustomerFeedback from "../components/CustomerFeedback";
import { mockProducts } from "../data/mockProducts";
import API from "../api";

export default function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        setError(null);
        
        // Try to fetch from MongoDB backend
        try {
          const res = await API.get("/products?limit=6");
          setProducts(res.data.products || res.data);
          setLoading(false);
        } catch (apiError) {
          // Fallback to mock data if API fails
          console.warn("API call failed, using mock data:", apiError.message);
          setTimeout(() => {
            setProducts(mockProducts.slice(0, 6));
            setLoading(false);
          }, 500);
        }
        
      } catch (err) {
        console.error("Error fetching home products:", err);
        setError("Failed to load products");
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      {/* Hero Section */}
      <Hero />

      {/* Our Products Section */}
      <section id="products-section" className="py-16 px-6 bg-base-200">
        <div className="container mx-auto max-w-6xl">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <h2 className="text-4xl font-bold mb-2 text-center">Our Products</h2>
            <p className="text-center text-lg opacity-70 mb-12">
              Explore our latest production orders and manufacturing capabilities
            </p>

            {/* Loading State */}
            {loading && (
              <div className="flex justify-center items-center py-20">
                <span className="loading loading-spinner loading-lg text-primary"></span>
              </div>
            )}

            {/* Error State */}
            {error && (
              <div className="text-center py-20">
                <p className="text-xl text-error mb-4">{error}</p>
                <p className="opacity-70">Please try again later</p>
              </div>
            )}

            {/* Products Grid */}
            {!loading && !error && (
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {products.map((product, index) => (
                  <motion.div
                    key={product._id}
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.5, delay: index * 0.1 }}
                    viewport={{ once: true }}
                  >
                    <ProductCard product={product} />
                  </motion.div>
                ))}
              </div>
            )}

            {/* Empty State */}
            {!loading && !error && products.length === 0 && (
              <div className="text-center py-20">
                <p className="text-xl opacity-70">No products available at the moment</p>
              </div>
            )}
          </motion.div>
        </div>
      </section>

      {/* How It Works Section */}
      <div id="how-it-works-section">
        <HowItWorks />
      </div>

      {/* Customer Feedback Section */}
      <CustomerFeedback />
    </div>
  );
}

import { useEffect, useState } from "react";
import { useParams, useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../context/AuthContext";

const ProductDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user, isAuthenticated } = useAuth(); // Assuming useAuth provides these
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        setLoading(true);
        const res = await axios.get(
          `${import.meta.env.VITE_API_URL}/products/${id}`
        );
        setProduct(res.data);
      } catch (err) {
        setError("Failed to load product details.");
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchProduct();
  }, [id]);

  const handleBookNow = () => {
    if (!isAuthenticated) {
      navigate("/login", { state: { from: `/products/${id}` } }); // Redirect to login, then back
      return;
    }
    navigate(`/booking/${id}`);
  };

  if (loading)
    return (
      <div className="flex justify-center items-center h-screen">
        <div className="loading loading-spinner loading-lg text-primary"></div>
      </div>
    );

  if (error)
    return (
      <div className="text-center py-20 text-red-500 text-xl font-bold">
        {error}
      </div>
    );

  if (!product)
    return <div className="text-center py-20">Product not found</div>;

  // Role Logic
  // Show button if: NOT logged in OR (Logged in AND role is 'buyer')
  // Block if: Role is admin/manager OR User is suspended
  const isBuyerOrGuest = !isAuthenticated || (user && user.role === "buyer");
  const isSuspended = user && user.status === "suspended";
  const canBook = isBuyerOrGuest && !isSuspended;

  // Determine button text/state
  let buttonText = "Book Now";
  let isDisabled = false;

  if (isAuthenticated && user.role !== "buyer") {
    buttonText = `Restricted to Buyers (${user.role})`;
    isDisabled = true;
  } else if (isSuspended) {
    buttonText = "Account Suspended";
    isDisabled = true;
  } else if (product.qty === 0) {
    buttonText = "Out of Stock";
    isDisabled = true;
  }

  return (
    <div className="container mx-auto px-4 py-10">
      <div className="flex flex-col md:flex-row gap-8 bg-white dark:bg-gray-800 shadow-xl rounded-2xl p-6 md:p-10">
        {/* Left: Image */}
        <div className="w-full md:w-1/2">
          <div className="carousel w-full rounded-xl overflow-hidden shadow-md">
            {product.images?.length > 0 ? (
              product.images.map((img, i) => (
                <div
                  key={i}
                  id={`slide${i}`}
                  className="carousel-item relative w-full h-96"
                >
                  <img
                    src={img}
                    className="w-full h-full object-cover"
                    alt={product.title}
                  />
                  {product.images.length > 1 && (
                    <div className="absolute flex justify-between transform -translate-y-1/2 left-5 right-5 top-1/2">
                      <a
                        href={`#slide${
                          i === 0 ? product.images.length - 1 : i - 1
                        }`}
                        className="btn btn-circle btn-sm opacity-70 hover:opacity-100"
                      >
                        â®
                      </a>
                      <a
                        href={`#slide${
                          i === product.images.length - 1 ? 0 : i + 1
                        }`}
                        className="btn btn-circle btn-sm opacity-70 hover:opacity-100"
                      >
                        â¯
                      </a>
                    </div>
                  )}
                </div>
              ))
            ) : (
              <img
                src="https://placehold.co/600x400?text=No+Image"
                className="w-full h-96 object-cover"
                alt="No Product"
              />
            )}
          </div>
          {/* Thumbnails if multiple (Optional enhancement) */}
        </div>

        {/* Right: Info */}
        <div className="w-full md:w-1/2 flex flex-col space-y-4">
          <div className="flex justify-between items-start">
            <div>
              <h1 className="text-4xl font-bold text-gray-900 dark:text-white leading-tight mb-2">
                {product.title}
              </h1>
              <div className="flex gap-2">
                <span className="badge badge-secondary">
                  {product.category}
                </span>
                {product.qty < 5 && product.qty > 0 && (
                  <span className="badge badge-warning">Low Stock</span>
                )}
              </div>
            </div>
            <div className="text-3xl font-bold text-green-600">
              ${product.price}
            </div>
          </div>

          <p className="text-gray-600 dark:text-gray-300 text-lg leading-relaxed border-b border-gray-200 dark:border-gray-700 pb-4">
            {product.desc}
          </p>

          <div className="grid grid-cols-2 gap-4 text-sm text-gray-500 dark:text-gray-400">
            <div className="flex flex-col p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
              <span className="uppercase text-xs font-bold mb-1">
                Available Quantity
              </span>
              <span className="text-lg font-semibold text-gray-900 dark:text-white">
                {product.qty}
              </span>
            </div>
            <div className="flex flex-col p-3 bg-gray-50 dark:bg-gray-700 rounded-lg">
              <span className="uppercase text-xs font-bold mb-1">
                Min Order Qty
              </span>
              <span className="text-lg font-semibold text-gray-900 dark:text-white">
                {product.minQty}
              </span>
            </div>
          </div>

          {product.demoVideo && (
            <div className="mt-2">
              <a
                href={product.demoVideo}
                target="_blank"
                rel="noopener noreferrer"
                className="link link-primary flex items-center gap-2"
              >
                ðŸŽ¥ Watch Demo Video
              </a>
            </div>
          )}

          <div className="mt-4">
            <span className="block text-sm font-bold text-gray-500 mb-2">
              Payment Options:
            </span>
            <div className="flex gap-2 flex-wrap">
              {product.paymentOptions?.map((opt) => (
                <span key={opt} className="badge badge-outline">
                  {opt}
                </span>
              ))}
            </div>
          </div>

          <div className="mt-auto pt-6">
            <button
              onClick={handleBookNow}
              disabled={isDisabled}
              className={`btn btn-lg w-full ${
                isDisabled ? "btn-disabled" : "btn-primary"
              } shadow-lg hover:shadow-xl transition-all`}
            >
              {buttonText}
            </button>
            {/* Warning for suspended logic */}
            {isSuspended && (
              <p className="text-error text-sm text-center mt-2">
                Your account is suspended.{" "}
                {user.suspendReason && `Reason: ${user.suspendReason}`}
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
