import { createContext, useContext, useState, useEffect } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { toast } from "react-toastify";
import axios from "axios";
import {
  auth,
  registerWithEmail,
  loginWithEmail,
  loginWithGoogle,
  loginWithGithub,
  logoutUser,
  updateUserProfile,
} from "../services/firebaseAuth";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check backend authentication (for superadmin)
  const checkBackendAuth = async () => {
    try {
      const res = await axios.get(`${import.meta.env.VITE_API_URL}/auth/verify`, {
        withCredentials: true
      });
      
      if (res.data.user) {
        setUser(res.data.user);
        setLoading(false);
        return true;
      }
    } catch (error) {
      // No backend auth, will check Firebase
      return false;
    }
    return false;
  };

  // Listen to Firebase auth state changes AND check backend auth
  useEffect(() => {
    const initAuth = async () => {
      // First check if logged in via backend (superadmin)
      const hasBackendAuth = await checkBackendAuth();
      
      if (hasBackendAuth) {
        return; // Already authenticated via backend
      }

      // Otherwise, check Firebase auth
      const unsubscribe = onAuthStateChanged(auth, (firebaseUser) => {
        if (firebaseUser) {
          setUser({
            uid: firebaseUser.uid,
            email: firebaseUser.email,
            displayName: firebaseUser.displayName,
            photoURL: firebaseUser.photoURL,
            role: firebaseUser.role || "user",
          });
        } else {
          setUser(null);
        }
        setLoading(false);
      });

      return () => unsubscribe();
    };

    initAuth();
  }, []);

  // Register with Email/Password
  const register = async (payload) => {
    try {
      const result = await registerWithEmail(payload);
      // Add role to user object (from registration form or default to 'user')
      setUser({ ...result.user, role: payload.role || "user" });
      toast.success("Account created successfully!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // Login with Email/Password
  const login = async (email, password) => {
    try {
      const result = await loginWithEmail(email, password);
      // Add default role. In production, fetch role from backend based on user ID
      setUser({ ...result.user, role: result.user.role || "user" });
      toast.success("Logged in successfully!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // Google OAuth
  const googleLogin = async () => {
    try {
      const result = await loginWithGoogle();
      const firebaseUser = result.user;
      // Properly extract photoURL from Firebase user object
      setUser({
        uid: firebaseUser.uid,
        email: firebaseUser.email,
        displayName: firebaseUser.displayName,
        photoURL: firebaseUser.photoURL,
        role: "user",
      });
      toast.success("Logged in with Google!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // GitHub OAuth
  const githubLogin = async () => {
    try {
      const result = await loginWithGithub();
      const firebaseUser = result.user;
      // Properly extract photoURL from Firebase user object
      setUser({
        uid: firebaseUser.uid,
        email: firebaseUser.email,
        displayName: firebaseUser.displayName,
        photoURL: firebaseUser.photoURL,
        role: "user",
      });
      toast.success("Logged in with GitHub!");
      return result;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  // Logout
  const logout = async () => {
    try {
      await logoutUser();
      setUser(null);
      toast.success("Logged out successfully!");
    } catch (err) {
      toast.error("Logout failed");
      throw err;
    }
  };

  // Update profile (name / photo)
  const updateProfileInfo = async ({ displayName, photoURL }) => {
    try {
      const updated = await updateUserProfile({ displayName, photoURL });
      // keep existing role while refreshing other fields
      setUser((prev) => ({
        ...(prev || {}),
        ...updated,
        role: prev?.role || "user",
      }));
      toast.success("Profile updated");
      return updated;
    } catch (err) {
      const message = getFirebaseErrorMessage(err);
      toast.error(message);
      throw err;
    }
  };

  const value = {
    user,
    setUser,
    login,
    register,
    googleLogin,
    githubLogin,
    logout,
    updateProfileInfo,
    loading,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Helper function to get user-friendly error messages
const getFirebaseErrorMessage = (error) => {
  switch (error.code) {
    case "auth/email-already-in-use":
      return "This email is already registered";
    case "auth/invalid-email":
      return "Invalid email address";
    case "auth/weak-password":
      return "Password should be at least 6 characters";
    case "auth/user-not-found":
      return "No account found with this email";
    case "auth/wrong-password":
      return "Incorrect password";
    case "auth/invalid-credential":
      return "Invalid email or password. Please check your credentials and try again.";
    case "auth/popup-closed-by-user":
      return "Sign-in popup was closed";
    case "auth/cancelled-popup-request":
      return "Only one popup request is allowed at a time";
    case "auth/popup-blocked":
      return "Popup was blocked by browser. Please allow popups for this site";
    default:
      return error.message || "Authentication failed";
  }
};

// Custom hook to use auth context
export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error("useAuth must be used within an AuthProvider");
  }
  return context;
};

